function searchRecipes() {
    const input = document.getElementById('search-bar').value.toLowerCase();
    const recipeBlocks = document.querySelectorAll('.recipe-block');
    
    recipeBlocks.forEach(recipeBlock => {
        const recipeItems = recipeBlock.querySelectorAll('li');
        let hasVisibleRecipe = false;
        
        recipeItems.forEach(recipe => {
            const recipeName = recipe.textContent.toLowerCase();
            if (recipeName.includes(input)) {
                recipe.style.display = '';
                hasVisibleRecipe = true;
            } else {
                recipe.style.display = 'none';
            }
        });
        
        recipeBlock.style.display = hasVisibleRecipe ? '' : 'none';
    });
} 