function searchRecipes(){const input=document.getElementById('search-bar').value.toLowerCase();const mainCoursesList=document.getElementById('main-courses-list').getElementsByTagName('li');const dessertsList=document.getElementById('dessert-list').getElementsByTagName('li');const mainCoursesDiv=document.querySelector('#main-courses-list').parentElement;const dessertsDiv=document.querySelector('#dessert-list').parentElement;function filterRecipes(list,sectionDiv){let hasVisibleRecipe=false;Array.from(list).forEach((recipe)=>{const recipeName=recipe.textContent.toLowerCase();if(recipeName.includes(input)){recipe.style.display='';hasVisibleRecipe=true}else{recipe.style.display='none'}});sectionDiv.style.display=hasVisibleRecipe?'':'none'}filterRecipes(mainCoursesList,mainCoursesDiv);filterRecipes(dessertsList,dessertsDiv)} 